import { useState } from 'react';
import ReactDOM from 'react-dom/client';

function MyForm() {
  const [name, setName] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(`The name you entered was: ${name}`)
  }

  return (
    <form className="new-poem-form" onSubmit={this.handleSubmit}>
        <input
          placeholder="Weather Analysis Summary"
          onChange={this.handleTitleChange}
        />
        <input
          placeholder="Pilot/Engineer"
          onChange={(e) => this.handleAuthorChange(e.target.value)}
        />
        <textarea
          placeholder="Weather Analysis"
          rows={5}
          onChange={(e) => this.handleContentChange(e.target.value)}
        />
        <input type="submit" value="Add weather analysis / Report" />
      </form>
  )
}

export default MyForm;
