import React from "react";

class WeatherAnalysis extends React.Component {

  constructor(props){
    super(props)

    this.state = {
      id: '',
      date: '',
      temperatureC: '',
      summary: '',
      temperatureF: ''
    }
  }

  submitHandler = e => {
    e.preventDefault()
    console.log(this.state)
  }

  changeHandler = (e) => {
    this.setState({[e.target.name]: e.target.value})
  }

  render() {
    const { id, date, temperatureC, summary, temperatureF } = this.state
    return (
      <div>
        <form onSubmit={this.submitHandler}>
          <div>
            <input> type="text" name="id" value={id} onChange={this.changeHandler}</input>
          </div>
          <div>
            <input> type="text" name="date" value={date} onChange={this.changeHandler}</input>
          </div>
          <div>
            <input> type="text" name="temperatureC" value={temperatureC} onChange={this.changeHandler}</input>
          </div>
          <div>
            <input> type="text" name="summary" value={summary} onChange={this.changeHandler}</input>
          </div>
          <div>
            <input> type="text" name="temperatureF" value={temperatureF} onChange={this.changeHandler}</input>
          </div>
          <button type="submit"></button>
        </form>
      </div>
    );
  }
}

export default WeatherAnalysis;


