import React from "react";

class WeatherAnalysis extends React.Component {
  state = {
    date: "",
    temperatureC: "",
    summary: "",
    temperatureF: "",
  };

  handleSubmit = (e) => {
    e.preventDefault();
    this.props.appendPiece(this.state);
  };

  handleAuthorChange = (value) => {
    this.setState({
      author: value,
    });
  };

  handleTitleChange = (e) => {
    console.log(e.target.value);
    this.setState({
      title: e.target.value,
    });
  };

  handleContentChange = (value) => {
    this.setState({
      content: value,
    });
  };

  render() {
    return (
      <form className="new-poem-form" onSubmit={this.handleSubmit}>
        <input
          placeholder="Add date using 2001-01-01 format"
          onChange={this.handleTitleChange}
        />
        <input
          placeholder="Temperatures recorded"
          onChange={(e) => this.handleAuthorChange(e.target.value)}
        />
        <textarea
          placeholder="Comments"
          rows={5}
          onChange={(e) => this.handleContentChange(e.target.value)}
        />
        <input type="submit" value="Add weather analysis / Report" />
      </form>
    );
  }
}

export default WeatherAnalysis;
