import Records from "../records.json";

function Comments() {
  return (
    <div className="Comments">
      Weather comments to the existing database
      <br /> <br />
      {Records.map((record) => {
        return (
          <div className="box" key={record.id}>
            <strong>{record.date}</strong>
            {record.temperatureC}
            {record.summary}
            {record.temperatureF}
          </div>
        );
      })}
    </div>
  );
}

export default Comments;

