import { useState } from "react";

const WeatherAnalysis = () => {
  const [date, setDate] = useState("");
  const [temperatureC, setTemperatureC] = useState("");
  const [summary, setSummary] = useState("");
};

const handleSubmit = (e) => {
  e.preventDefault();
  const blog = { date, temperatureC, summary };

  fetch("", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(blog)
  }).then(() => {
    console.log("New  weather comment added");
  })

    return (
      <form className="new-poem-form" onSubmit={handleSubmit}>
        <input
          placeholder="Add date using 2001-01-01 format"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
        <input
          placeholder="Temperatures recorded"
          value={temperatureC}
          onChange={(e) => setTemperatureC(e.target.value)}
        />
        <textarea
          placeholder="Comments"
          rows={5}
          value={summary}
          onChange={(e) => setSummary(e.target.value)}
        />
        <input type="submit" value="Add weather analysis / Report" />
      </form>
    );
};


export default WeatherAnalysis;
